import { SEARCH_MOVIE } from "../actions/types";

const initialState ={
    text :'',
    movies : [], 
    loading : false,
    movie: []
}


function  searchReducer(state = initialState, action){
    switch(action.type){ 
        case SEARCH_MOVIE:
            return {
                ...state,
                text:action.payload,
                loading:false
            }
            
        }
    }
