import { SEARCH_MOVIE,FETCH_MOVIES } from "../actions/types";
import axios from 'axios'

const APIKey= require('../components/APIKey')

export const searchMovie = text => dispatch =>{
    dispatch({
        type:SEARCH_MOVIE,
        payload:text
    })
}



export const fetchMovie = text => dispatch =>{
    axios
        .get(`http://www.omdbapi.com/?apikey=${APIKey}&s=${text}`)
       // console.log(`http://www.omdbapi.com/?apikey={APIKey}&s=${text}`)
          .then(response => 
                dispatch({
                    type:FETCH_MOVIES,
                    payload:response.data   
          }))
          .catch (error =>console.log(error))
}
