import React, { Component } from 'react'

import {searchMovie,fetchMovie} from '../../actions/searchAction'

import {connect} from 'react-redux'
import searchReducer from '../../reducers/searchReducer'

class SearchForm extends Component {

    onChange= e=>{
        this.props.searchMovie(e.target.value)
    }

    onSubmit = e =>{
        e.preventDefault();
        this.props.fetchMovies(this.props.text)
    }

    render() {
        return (
            <div className="jumbotron jumbotron-fluid mt-5 text-center">
                <div className ="container">
                    <h1 className="display-4 mb-4">
                        <i className="fa fa-search" /> Search for a movie, TV series ..
                    </h1>
                    <form id="searchForm" onSubmit={this.onSubmit.bind(e)}>
                        <input 
                            type="text"
                            className="form-control"
                            name="searchText"
                            placeholder="Search for a movie, TV series .."
                            onChange={this.onChange}
                            />
                            <button type="submit" className="btn btn-primary btn-bg mt-3">
                                Search
                            </button>
                    </form>
                </div>
            </div>
        )
    }
}


const mapStateToProps =state =>({
    text:state.movies.text
})


export default connect(mapStateToProps,{searchMovie})(SearchForm)
